  return (
    <SafeAreaView style={Styles.container}>
        <ScrollView style={{flex:1}}>
            <View style={Styles.header}>
                <Text style={Styles.HomeText}>레시피</Text>
                <View style = {Styles.Back}>
                    <TouchableOpacity onPress={() => alert('뒤로가기')}>
                        <Icon name = "close" size = {20}/>
                    </TouchableOpacity>
                  </View>
            </View>
            <View style = {Styles.TextForm}>
            {
          UserQuestion == 'null' ? (<Icon2 style = {Styles.Icon2} name = "magnifier" size = {17}/>) : (<Icon2 style = {Styles.Icon22} name = "magnifier" size = {17}/>)
          }
            <TextInput
                  style = {Styles.Text}
                  placeholder = "검색어를 입력해주세요"
                  onChangeText={(text) => setUserQuestion(text)}
                  onSubmitEditing={() => GptQ()}
              />
            </View>
            <View>
            {data.map((item, index) => (
                <Text key={index}>{item}</Text>
            ))}
        </View>
            <View>
              <Text style={Styles.ingredient}>내가 가져온 재료</Text>
            </View>
            <Text style={Styles.ingredient}>유통기한 임박한 재료</Text>
            <View style = {Styles.Icon}>
                    <TouchableOpacity
                      onPress={() => alert('뒤로가기')}
                      
                    >
                        <Chat name = "chatbox" size = {50}/>
                    </TouchableOpacity>
                </View>
        </ScrollView>
    </SafeAreaView>
  )
}

export default RecipeMain;

const AllWidth = Dimensions.get("window").width;
const AllHeight = Dimensions.get("window").height;

const FigmaWidth = 390;
const FigmaHeight = 844;

const BasicWidth =(
    AllWidth / FigmaWidth
).toFixed(2);

const BasicHeight =(
    AllHeight / FigmaHeight
).toFixed(2);

const Styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  header:{
    height: 59 * BasicHeight,
    width: AllWidth,
    //backgroundColor: "#BECFF3",
    flexDirection: 'row',
    //alignSelf: 'center',
    //alignContent: 'center',
    justifyContent: 'center',
  },
  HomeText: {
