  },
  Icon:{
    alignSelf: 'center',
    marginBottom: BasicHeight*35,
  },
  Icon2:{
    marginTop: BasicHeight*18,
    color: '#E2E2E2',
  },
  Icon22:{
    marginTop: BasicHeight*18,
    color: '#000000',
  },
  Back:{
    //alignSelf: 'flex-end',
    marginTop: BasicHeight*13,
    marginLeft: BasicWidth*129,
