import React from "react";
import { View, Text, StyleSheet, TouchableOpacity } from "react-native";
import { useNavigation } from "@react-navigation/native";

const Signin = () => {

  const navigation = useNavigation();

  return (
    <View style={Styles.container}>      
      <Text style={Styles.HomeText}>로그인 화면</Text>
        <View style = {Styles.titleArea}>
                <View style = {Styles.title}>
                    <Text>Login</Text>
                </View>
                <View >
                    <TextInput 
                         
                        placeholder={"ID"}/>
                    <TextInput 
                         
                        placeholder={"Password"}/>
                </View>
                <View>
                <TouchableOpacity 
                  activeOpacity={0.8} 
                  onPress ={()=> navigation.navigate('Splash', {screen : 'Splash'})}>
                    <Text>Login</Text>
                </TouchableOpacity>
                </View>
            </View>
        <TouchableOpacity
          onPress={() => navigation.navigate("Signup", { screen: 'Signup' })}
          style={Styles.NextBottom}
        >
          <Text style={Styles.BottomText}>회원가입 화면으로</Text>
          </TouchableOpacity>
      <TouchableOpacity
          onPress={() => navigation.navigate("KaKaoLogin", { screen: 'KaKaoLogin' })}
          style={Styles.NextBottom}
        >
          <Text style={Styles.BottomText}>카카오 화면으로</Text>
        </TouchableOpacity>
    </View>
  )
}

export default Signin;

const Styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  HomeText: {
    fontSize: 30,
    textAlign: "center",
  },
  NextBottom: {
    backgroundColor: "purple",
    padding: 10,
    marginTop: "20%",
    width: "50%",
    alignSelf: "center",
    borderRadius: 10,
  },
  BottomText: {
    fontSize: 15,
    color: 'white',
    textAlign: "center",
  },
  titleArea: {
    width: '100%',
    padding: wp('10%'),
    alignItems: 'center',
  },
  title: {
      fontSize: 36 ,
  },
  formArea: {
      width: '100%',
      paddingBottom: wp('10%'),
  },
  textForm: {
      borderWidth: 0.5,
      borderColor: '#888',
      width: '100%',
      height: hp('5%'),
      paddingLeft: 5,
      paddingRight: 5,
      marginBottom: 5,
  },
  buttonArea: {
      width: '100%',
      height: hp('5%'),
  },
  button: {
      backgroundColor: "#46c3ad",
      width: "100%",
      height: "100%",
      justifyContent: 'center',
      alignItems: 'center',
  },
  buttonTitle: {
      color: 'white',
  },
})
