import React, { useEffect } from "react";
import { View, Text, StyleSheet, TouchableOpacity } from "react-native";
import { useNavigation } from "@react-navigation/native";
import AsyncStorage from '@react-native-async-storage/async-storage';
import {createRef, useState} from 'react';
import {
    TextInput,
    Image,
    ScrollView
} from 'react-native';

const Signup = () => {

  const navigation = useNavigation();

  useEffect(() => {

    async function getData() {
      try {
        const value = await AsyncStorage.getItem('userAccessToken')
        if(value !== null) {
          console.log(value);
        }
      } catch(e) {
        console.log('error', value);
      }
    }
    getData();
  },[]);

    const [userEmail, setUseremail] = useState("");
    const [userName, setUsername] = useState("");
    const [userPassword, setUserpassword] = useState("");
    const [userBirth, setUserbirth] = useState("");
    const [userPnum, setUserpnum] = useState("");

    const [errortext, setErrortext] = useState('');
    const [isRegistraionSuccess, setIsRegistraionSuccess] = useState(false);
/*
    const idInputRef = createRef();
    const gradeInputRef = createRef();
    const passwordInputRef = createRef();
    const passwordchkInputRef = createRef();
    const nameInputRef = createRef();
*/
    const SubmitButton = () => {
        setErrortext('');

        if(!userEmail){
            alert('이메일을 입력해주세요');
            return;
        }
        if(!userPassword){
            alert('비밀번호를 입력해주세요');
            return;
        }
        if(!userName){
            alert('이름을 입력해주세요');
            return;
        }
        if(!userBirth){
            alert('생년월일을 입력해주세요');
            return;
        }
        if(!userPnum){
            alert('전화번호를 입력해주세요');
            return;
        }

        var dataToSend = {
            db_Email : userEmail,
            db_Password : userPassword,
            db_Name : userName,
            db_Birth : userBirth,
            db_Pnum : userPnum,
        };

        var formBody = [];
        for (var key in dataToSend){
            var encodedKey = encodeURIComponent(key);
            var encodedValue = encodeURIComponent(dataToSend(key));
            formBody.push(encodedKey + '=' + encodedValue)
        }
        formBody =  formBody.join('&');

        fetch('./api/members/register',
        {
            method : 'POST',
            body : formBody,
            headers : {
                'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8',
            },
        })
        .then((response) => response.json())
        .then((responseJson) => {
            //Hide Loader
            setLoading(false);
            setErrortext2('');
            console.log(responseJson);
            // If server response message same as Data Matched
            if (responseJson.status === 'success') {
            setIsRegistraionSuccess(true);
            console.log('Registration Successful. Please Login to proceed');
            } else if (responseJson.status === 'duplicate') {
            setErrortext2('이미 존재하는 아이디입니다.');
            }
        })
        .catch((error) => {
            //Hide Loader
            setLoading(false);
            console.error(error);
      });
  };
    
   
/*

rules={
                          required = "비밀번호를 확인해주세요",
                            validate = {
                              matchPassword: (value) => {
                                const { password } = getValues();
                                return password === value || '비밀번호가 일치하지 않습니다'
                              }
                            }
                        }
                        
