  return (
    <View style={Styles.container}>      
      return (
        <View>
            <View>
                <Text>회원가입</Text>
            </View>
            <View>
                <TextInput
                    placeholder={"이메일"}
                    value = {userEmail}
                    onChangeText = {setUseremail}
                    />
                <TextInput
                    placeholder={"비밀번호"}
                    value = {password}
                    onChangeText = {setUserpassword}
                    secureTextEntry
                    />
                    
            </View>
            <View>
                <TextInput 
                    placeholder= "이름"
                    value = {userName}
                    onChangeText = {setUsername}
                    />
                <TextInput 
                    placeholder= "생년월일"
                    value = {userBirth}
                    onChangeText = {setUserbirth}
                    />
                <TextInput 
                    placeholder= "전화번호"
                    value = {userPnum}
                    onChangeText = {setUserpnum}
                    />
            </View>
            <TouchableOpacity activeOpacity={0.8} onPress ={()=> this.props.navigation.navigate('Login')}>
                <Text>회원가입</Text>
            </TouchableOpacity>
        </View>
    );
      <TouchableOpacity
          onPress={() => navigation.navigate( "Home", { screen: "Home"} )}
          style={Styles.NextBottom}
        >
          <Text style={Styles.BottomText}>홈 화면으로</Text>
        </TouchableOpacity>
    </View>
  )
}

export default Signup;

const Styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: 24,
    backgroundColor: '#fff',
  },
  HomeText: {
    marginTop: 100,
    fontSize: 30,
    textAlign: "center",
  },
  NextBottom: {
    backgroundColor: "purple",
    padding: 10,
    marginTop: "20%",
    width: "50%",
    alignSelf: "center",
    borderRadius: 10,
  },
  BottomText: {
    fontSize: 15,
    color: 'white',
    textAlign: "center",
  }
})
