    includeFontPadding: false,
    fontFamily: 'NotoSansKR-Regular',
  },
  retrieveButton: {
    padding: 5,
    marginBottom:200,
    marginTop:20,
    marginLeft:106,
  },
  retrieveButtonText: {
    textDecorationLine: 'underline',
